import { Component, OnInit,ElementRef, ViewChild } from '@angular/core';
import { ActivatedRoute, Router, NavigationExtras } from '@angular/router';
import { AnimationController} from '@ionic/angular';

@Component({
  selector: 'app-incorrecta',
  templateUrl: './incorrecta.page.html',
  styleUrls: ['./incorrecta.page.scss'],
})
export class IncorrectaPage implements OnInit {

  @ViewChild('titulo', { read: ElementRef }) itemTitulo!: ElementRef;
    @ViewChild('video')
  private video!: ElementRef;

  constructor(private router: Router, private animationController: AnimationController) { 
  }

  public animateItem(elementRef: any) {
    this.animationController
      .create()
      .addElement(elementRef)
      .iterations(1)
      .duration(600)
      .fromTo('transform', 'translate(100%)', 'translate(0%)')
      .play();
  }


  public ngOnInit(): void {
  }

  public ngAfterViewInit(): void {
    if (this.itemTitulo) {
      const animation = this.animationController
        .create()
        .addElement(this.itemTitulo.nativeElement)
        .iterations(Infinity)
        .duration(3000)
        .fromTo('transform', 'translate(-100%)', 'translate(100%)')
        .fromTo('opacity', 1, 1);

      animation.play();
    }
  }
  public home(){
    this.router.navigate(['/login']);
  }
}
<ion-header>
  <ion-toolbar class="headercolor">
    <div #titulo class="centratit">
      <ion-buttons slot="start">
        <ion-img class="imglogo" src="assets/img/DuocUC.svg.png"></ion-img>

        <ion-title class="ion-text-center">
          Asistencia Duoc
        </ion-title>
      </ion-buttons>
    </div>
  </ion-toolbar>
</ion-header>

<ion-content class="background" [fullscreen]="true">
  <div style="height: 80%; display: flex; align-items: center; justify-content:center;">
    <ion-card class="colorcard1">
      <div style="display: flex; align-items: center; justify-content:center;">
        <ion-img class="imagena ion-padding-vertical" src="assets/img/fotoincorrect.png"></ion-img>
      </div>
      <ion-card-title>
        <ion-label class="ion-text-center">
          <!--mensaje-->
          <h1 class="azul">No fue posible recuperar tu contraseña</h1>
        </ion-label>
      </ion-card-title>
      <ion-card-content class="ion-text-center">
        <ion-button class="botonduoc" (click)="home()">Ingresar</ion-button>
      </ion-card-content>
    </ion-card>
  </div>

</ion-content>
